import React, { Component } from 'react'
import Item from '../../components/pages/Product/Item'

//redux 配置
import {connect} from 'react-redux'
import * as productsActionCreater from '../../redux/actions/products'

import './index.css'

class Home extends Component {
    render() {
        return (
            <div className='home_page'>
                <h2 className='home_page_title'>Hot Sales:</h2>
                {this.props.productsActionCreater}
                <div className='hot_products'>
                    <Item/>

                </div>

            </div>
        )
    }
}
export default connect(
    state=>({
        productsState: state.productsState
    }),
    {
        productsActionCreater
    }
)(Home)
