const initState = {itemArrInCart: []}
export default function CartReducer(preState=initState, action){
    const {type, data} = action

    switch (type) {
        case constants.SAVE_TO_CART:

            const doesItemExists = preState.itemArrInCart.find(item => item.id === data.id)

            if(doesItemExists){
                return{
                    // 目前只有一个itemArrInCart在里面, 我们以后可能加更多的properties,在这里先展开
                    ...preState,
                }
            }

            return ;

        case constants.DELETE_FROM_CART:

            return ;

        case constants.RESET_CART:

            return ;

        default:
            return preState;
    }
}
