import * as constants from '../constants/product'

const initState = {productsArr: [], isLoading: false, err: ''}
export function ProductReducer(preState=initState, action){
    const {type, data} = action

    switch (type) {
        case constants.GET_PRODUCTS_REQ:

            return{
                isLoading: true,
                productsArr: []
            };

        case constants.GET_PRODUCTS_SUCCESS:

            return{
                isLoading: false,
                productsArr: data
            };

        case constants.GET_PRODUCTS_ERROR:

            return{
                isLoading: false,
                err: data
            };

        default:
            return preState;
    }
}
